AND img_key like #{keyword} || '%'
AND doc_cd like #{keyword} || '%'
) WHERE NUM BETWEEN #{startList}+1 and #{startList}+1+#{listSize}-1 group by img_key, cust_no, cust_nm, doc_cd, enr_user_id, enr_org_cd, rrn_no order by img_key

